'use-strict';

//Parse Data, creates bots and sorts them by killing priority
var createBots = function (lines) {
  return lines.map(function (line) {
      var tmp = line.split(" ").map(function (val, idx) {
        return (idx == 0) ? val : parseInt(val);
      });
      tmp.push(Math.ceil(tmp[1]/tmp[2]));
      return tmp;
    }).sort(function (a, b) {
      return (a[3] === b[3]) ? 0 : ((a[3] < b[3]) ? -1 : 1);
    });
}

module.exports = createBots;
