'use strict'

//Checks if the game is beatable, if the enemies will reach the tower
//before they can get killed because there were too many, the game is unbeatable
var possibleToWin = function (bots) {
  var length = bots.length;

  for (var i = 0; i < length; i++) {
    if (bots[i][3] <= i) {
      return false;
    }
  }
  return true;
}

module.exports = possibleToWin;
